import React, { useState, useEffect } from 'react';

const PersonLoadingAnimation = () => {
  const [currentFrame, setCurrentFrame] = useState(0);

  const frames = [
    {
      id: 0,
      description: "Opening Scene - Person visible in frame, indoor setting with moderate lighting",
      className: "frame-0"
    },
    {
      id: 1,
      description: "Shift in Expression - Person adjusts posture, speaking or reacting",
      className: "frame-1"
    },
    {
      id: 2,
      description: "Body Movement - Person leans forward or turns, camera remains fixed",
      className: "frame-2"
    },
    {
      id: 3,
      description: "Midway Scene - Person continues talking, hands more animated",
      className: "frame-3"
    },
    {
      id: 4,
      description: "Background Still - Subject active, gestures and expressions changing",
      className: "frame-4"
    },
    {
      id: 5,
      description: "Stronger Action - More expressive gesture, moment of emphasis",
      className: "frame-5"
    },
    {
      id: 6,
      description: "Engaged Interaction - Animated movement, explaining or reacting",
      className: "frame-6"
    },
    {
      id: 7,
      description: "Closing Scene - Person in focus, natural conclusion",
      className: "frame-7"
    }
  ];

  useEffect(() => {
    // Total duration: 2.5 seconds, 8 frames
    // Each frame should last: 2.5 / 8 = 0.3125 seconds = 312.5ms
    const frameDuration = 312.5;

    const interval = setInterval(() => {
      setCurrentFrame(prev => {
        if (prev >= frames.length - 1) {
          return 0; // Loop back to start
        }
        return prev + 1;
      });
    }, frameDuration);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="person-loading-animation">
      <div className="animation-container">
        {/* Indoor background */}
        <div className="indoor-background">
          <div className="room-outline"></div>
          <div className="lighting-effect"></div>
        </div>

        {/* Person figure */}
        <div className={`person-figure ${frames[currentFrame].className}`}>
          <div className="head"></div>
          <div className="body"></div>
          <div className="arms"></div>
          <div className="legs"></div>
        </div>

        {/* Frame indicator */}
        <div className="frame-indicator">
          <span className="frame-number">Frame {currentFrame + 1}/8</span>
          <span className="frame-description">{frames[currentFrame].description}</span>
        </div>
      </div>
    </div>
  );
};

export default PersonLoadingAnimation;
