import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { cn } from '../lib/utils';

export const Card = React.memo(
  ({
    car,
    index,
    hovered,
    setHovered,
  }) => {
    const { id, title, brand, type, transmission, fuel, pricePerDay, seats, mileageKm, images, featured, description } = car;
    const imageUrl = images && images.length > 0 ? images[0] : '/img/placeholder.jpg';

    return (
      <div
        onMouseEnter={() => setHovered(index)}
        onMouseLeave={() => setHovered(null)}
        className={cn(
          "rounded-lg relative bg-gray-100 dark:bg-neutral-900 overflow-hidden h-60 md:h-96 w-full transition-all duration-300 ease-out cursor-pointer",
          hovered !== null && hovered !== index && "blur-sm scale-[0.98]"
        )}
      >
        <img
          src={imageUrl}
          alt={title}
          className="object-cover absolute inset-0 w-full h-full"
          onError={(e) => {
            e.target.src = '/img/placeholder.jpg';
          }}
        />

        {/* Overlay with car info - only visible on hover */}
        <div
          className={cn(
            "absolute inset-0 bg-black/50 flex items-end py-8 px-4 transition-opacity duration-300",
            hovered === index ? "opacity-100" : "opacity-0"
          )}
        >
          <div className="text-xl md:text-2xl font-medium bg-clip-text text-transparent bg-gradient-to-b from-neutral-50 to-neutral-200">
            {title}
          </div>
        </div>

        {/* Featured badge - always visible */}
        {featured && (
          <div className="absolute top-4 right-4 bg-yellow-500 text-black px-3 py-1 rounded-full text-sm font-semibold z-10">
            Featured
          </div>
        )}
      </div>
    );
  }
);

Card.displayName = 'Card';

export function FocusCarCards({ cars }) {
  const [hovered, setHovered] = useState(null);

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-10 max-w-5xl mx-auto md:px-8 w-full">
      {cars.map((car, index) => (
        <Card
          key={car.id}
          car={car}
          index={index}
          hovered={hovered}
          setHovered={setHovered}
        />
      ))}
    </div>
  );
}
