import React, { useState } from "react";

const faqs = [
  {
    question: "What is Chalyati car subscription?",
    answer: "Chalyati offers flexible car subscriptions where you can drive a car without the commitment of ownership. It includes insurance, maintenance, and roadside assistance.",
    icon: "ðŸš—"
  },
  {
    question: "How long can I subscribe to a car?",
    answer: "We offer flexible subscription periods starting from 3 months up to 36 months, with easy extension options available.",
    icon: "â°"
  },
  {
    question: "What documents do I need?",
    answer: "You'll need a valid driving license, PAN card, Aadhaar card, and address proof. Business customers need additional company documents.",
    icon: "ðŸ“‹"
  },
  {
    question: "Is insurance included?",
    answer: "Yes, comprehensive insurance is included in your subscription with zero additional cost.",
    icon: "ðŸ›¡ï¸"
  },
  {
    question: "What about maintenance and service?",
    answer: "All scheduled maintenance and wear & tear repairs are covered under your subscription plan.",
    icon: "ðŸ”§"
  },
  {
    question: "Can I change cars during subscription?",
    answer: "Yes, you can upgrade or change your car during the subscription period with our flexible exchange program.",
    icon: "ðŸ”„"
  }
];

const FAQ = () => {
  const [openIndex, setOpenIndex] = useState(null);

  const toggleFAQ = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <section className="faq-section">
      <div className="faq-background">
        <div className="faq-particles"></div>
      </div>

      <div className="faq-content">
        <div className="faq-header">
          <h2 className="faq-title">Frequently Asked Questions</h2>
          <div className="faq-underline"></div>
          <p className="faq-subtitle">
            Get answers to common questions about Chalyati car subscriptions
          </p>
        </div>

        <div className="faq-grid">
          {faqs.map((faq, index) => (
            <div 
              key={index} 
              className={`faq-card ${openIndex === index ? 'active' : ''}`}
              onClick={() => toggleFAQ(index)}
            >
              <div className="faq-card-header">
                <div className="faq-icon">{faq.icon}</div>
                <h3 className="faq-question">{faq.question}</h3>
                <div className={`faq-toggle ${openIndex === index ? 'active' : ''}`}>
                  <span className="faq-plus">+</span>
                  <span className="faq-minus">âˆ’</span>
                </div>
              </div>

              <div className={`faq-answer ${openIndex === index ? 'open' : ''}`}>
                <div className="faq-answer-content">
                  <p>{faq.answer}</p>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className="faq-cta">
          <div className="faq-cta-card">
            <div className="faq-cta-icon">ðŸ’¬</div>
            <h3>Still have a question?</h3>
            <p>Our experts are here to help you with any queries</p>
            <a href="https://wa.me/919876543210" target="_blank" rel="noopener noreferrer" className="faq-cta-button">
              <span>Talk to an Expert</span>
              <div className="faq-cta-arrow">â†’</div>
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default FAQ;
