import React from 'react';
import DotGrid from "../components/DotGrid";
import FAQ from "../components/FAQ";
import FeaturedCarsBlock from "../components/FeaturedCarsBlock";
import Hero from '../components/Hero';
import HowItWorksBig from "../components/HowItWorksBig";
import MediaCoverage from "../components/MediaCoverage";
import SaveWithSubscription from "../components/SaveWithSubscription";
import Testimonials from "../components/Testimonials";
import USPGrid from "../components/USPGrid";


const Home = () => {

  return (
    <div className="main">
      <div className="container">
        {/* Hero Section */}
        <Hero />

        {/* DotGrid Background for all sections after Hero */}
        <div style={{ 
          position: 'fixed', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100vh', 
          zIndex: -1,
          pointerEvents: 'auto'
        }}>
          <DotGrid
            dotSize={12}
            gap={25}
            baseColor="#333333"
            activeColor="#5227FF"
            proximity={150}
            shockRadius={250}
            shockStrength={5}
            resistance={750}
            returnDuration={1.5}
          />
        </div>

        {/* Extra Sections (just added, not replacing anything) */}
        <USPGrid />
        <HowItWorksBig />
        <SaveWithSubscription />
        <FeaturedCarsBlock />
        <Testimonials />
        <MediaCoverage />
        <FAQ />

        {/* How It Works (your old inline one) */}
        <section className="how-it-works full-viewport">
          <h2 className="section-title">How It Works</h2>
          <div className="steps-grid">
            <div className="step-card">
              <div className="step-number">1</div>
              <h3 className="step-title">Choose Your Car</h3>
              <p className="step-description">
                Browse our extensive fleet and select the perfect vehicle for your needs.
              </p>
            </div>
            <div className="step-card">
              <div className="step-number">2</div>
              <h3 className="step-title">Book & Confirm</h3>
              <p className="step-description">
                Make your reservation and receive instant confirmation for your booking.
              </p>
            </div>
            <div className="step-card">
              <div className="step-number">3</div>
              <h3 className="step-title">Enjoy Your Ride</h3>
              <p className="step-description">
                Pick up your car and enjoy a comfortable, safe journey with our well-maintained vehicles.
              </p>
            </div>
          </div>
        </section>

        {/* Special Offers */}
        <section className="offers-section full-viewport">
          <h2 className="section-title">Special Offers</h2>
          <div className="offers-grid">
            {[
              "Weekly and monthly rental discounts",
              "Corporate fleet rental packages",
              "Long-term rental special rates",
              "Weekend getaway packages",
              "Student and senior citizen discounts",
              "Loyalty program benefits",
            ].map((offer, idx) => (
              <div key={idx} className="offer-card">
                <div className="offer-icon">âœ“</div>
                <p className="offer-text">{offer}</p>
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
