import React, { useState, useEffect } from "react";

const testimonials = [
  {
    name: "Samarth Chaturvedi",
    text: "Took a Chalyati subscription for 6 monthsâ€”smooth process, car in great condition, and very helpful support.",
    rating: 4.5,
    avatar: "ðŸ‘¨â€ðŸ’¼"
  },
  {
    name: "Vaibhav Kaushal", 
    text: "Awesome service and quick delivery within 5 days. Better pricing compared to competitors.",
    rating: 5,
    avatar: "ðŸ‘¨â€ðŸŽ“"
  },
  {
    name: "Rhonen Gurung",
    text: "Using for about a monthâ€”car delivered in peak condition, team is prompt and professional.",
    rating: 5,
    avatar: "ðŸ‘¨â€ðŸš€"
  },
  {
    name: "Priya Sharma",
    text: "Excellent customer service and well-maintained vehicles. Highly recommend for business travel.",
    rating: 5,
    avatar: "ðŸ‘©â€ðŸ’»"
  },
  {
    name: "Rajesh Kumar",
    text: "Great experience with Chalyati. The booking process is simple and cars are always clean.",
    rating: 4.5,
    avatar: "ðŸ‘¨â€ðŸ’»"
  }
];

const StarRating = ({ rating }) => {
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;

  return (
    <div className="star-rating">
      {Array.from({ length: fullStars }).map((_, i) => (
        <span key={i} className="star">â˜…</span>
      ))}
      {hasHalfStar && <span className="star half">â˜†</span>}
    </div>
  );
};

const Testimonials = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isVisible, setIsVisible] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => {
      setIsVisible(false);

      setTimeout(() => {
        setCurrentIndex((prevIndex) => 
          prevIndex === testimonials.length - 1 ? 0 : prevIndex + 1
        );
        setIsVisible(true);
      }, 300);
    }, 4000);

    return () => clearInterval(interval);
  }, []);

  const currentTestimonial = testimonials[currentIndex];

  return (
    <section className="testimonials-section">
      <div className="container">
        <div className="testimonials-header">
          <h2 className="section-title">What Our Customers Say</h2>
          <p className="testimonials-subtitle">Real reviews from satisfied customers</p>
        </div>

        <div className="testimonials-content">
          <div className={`testimonial-card ${isVisible ? 'visible' : 'hidden'}`}>
            <div className="testimonial-avatar">
              {currentTestimonial.avatar}
            </div>

            <div className="testimonial-content">
              <StarRating rating={currentTestimonial.rating} />

              <blockquote className="testimonial-text">
                "{currentTestimonial.text}"
              </blockquote>

              <div className="testimonial-author">
                <strong>{currentTestimonial.name}</strong>
              </div>
            </div>
          </div>

          <div className="testimonials-indicators">
            {testimonials.map((_, index) => (
              <button
                key={index}
                className={`indicator ${index === currentIndex ? 'active' : ''}`}
                onClick={() => {
                  setIsVisible(false);
                  setTimeout(() => {
                    setCurrentIndex(index);
                    setIsVisible(true);
                  }, 300);
                }}
                aria-label={`Go to testimonial ${index + 1}`}
              />
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
