import React, { useEffect, useState } from 'react';
import AdminNavbar from '../components/AdminNavbar';
import { useCars } from '../state/CarsContext';

const Admin = () => {
  const { cars, addCar, updateCar, deleteCar, loading, error, clearError } = useCars();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [password, setPassword] = useState('');
  const [editingCar, setEditingCar] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState('all');
  const [filterBrand, setFilterBrand] = useState('all');
  const [showForm, setShowForm] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');
  const [activeSection, setActiveSection] = useState('dashboard');
  const [formData, setFormData] = useState({
    title: '',
    brand: '',
    type: '',
    transmission: '',
    fuel: '',
    pricePerDay: '',
    seats: '',
    mileageKm: '',
    yearOfManufacture: '',
    ownerName: '',
    contactNumber: '',
    images: '',
    featured: false,
    description: ''
  });

  // Dynamically generate options from actual cars data
  const brands = [...new Set(cars.map(car => car.brand))].sort();
  const types = [...new Set(cars.map(car => car.type))].sort();
  const transmissions = [...new Set(cars.map(car => car.transmission))].sort();
  const fuels = ["Petrol", "Diesel", "EV", "Hybrid"];

  // Dashboard statistics
  const totalCars = cars.length;
  const featuredCars = cars.filter(car => car.featured).length;
  const totalValue = cars.reduce((sum, car) => sum + (car.pricePerDay || 0), 0);
  const avgPrice = totalCars > 0 ? Math.round(totalValue / totalCars) : 0;

  // Filtered cars
  const filteredCars = cars.filter(car => {
    const matchesSearch = car.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         car.brand.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = filterType === 'all' || car.type === filterType;
    const matchesBrand = filterBrand === 'all' || car.brand === filterBrand;
    return matchesSearch && matchesType && matchesBrand;
  });

  // Check if user is already logged in
  useEffect(() => {
    const adminSession = localStorage.getItem('admin-session');
    if (adminSession) {
      setIsLoggedIn(true);
    }
  }, []);

  // Auto-hide success messages
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage('');
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  // Auto-hide error messages
  useEffect(() => {
    if (error) {
      const timer = setTimeout(() => {
        clearError();
      }, 8000);
      return () => clearTimeout(timer);
    }
  }, [error, clearError]);

  const handleLogin = (e) => {
    e.preventDefault();
    if (password === 'admin123') { // Enhanced password
      setIsLoggedIn(true);
      setPassword('');
      localStorage.setItem('admin-session', 'true');
    } else {
      alert('Incorrect password. Please try again.');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem('admin-session');
    resetForm();
    clearError();
    setSuccessMessage('');
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const resetForm = () => {
    setFormData({
      title: '',
      brand: '',
      type: '',
      transmission: '',
      fuel: '',
      pricePerDay: '',
      seats: '',
      mileageKm: '',
      yearOfManufacture: '',
      ownerName: '',
      contactNumber: '',
      images: '',
      featured: false,
      description: ''
    });
    setEditingCar(null);
    setShowForm(false);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      let success = false;

      if (editingCar) {
        success = updateCar({ ...formData, id: editingCar.id });
        if (success) {
          setSuccessMessage('Car updated successfully!');
        }
      } else {
        success = addCar(formData);
        if (success) {
          setSuccessMessage('Car added successfully!');
        }
      }

      if (success) {
        resetForm();
      }
    } catch (error) {
      console.error('Error submitting form:', error);
    }
  };

  const handleEdit = (car) => {
    setEditingCar(car);
    setFormData({
      title: car.title,
      brand: car.brand,
      type: car.type,
      transmission: car.transmission,
      fuel: car.fuel,
      pricePerDay: car.pricePerDay.toString(),
      seats: car.seats.toString(),
      mileageKm: car.mileageKm.toString(),
      yearOfManufacture: car.yearOfManufacture?.toString() || '',
      ownerName: car.ownerName || '',
      contactNumber: car.contactNumber || '',
      images: Array.isArray(car.images) ? car.images.join(', ') : car.images,
      featured: car.featured,
      description: car.description
    });
    setShowForm(true);
  };

  const handleDelete = async (carId) => {
    if (window.confirm('Are you sure you want to delete this car? This action cannot be undone.')) {
      try {
        const success = deleteCar(carId);
        if (success) {
          setSuccessMessage('Car deleted successfully!');
        }
      } catch (error) {
        console.error('Error deleting car:', error);
      }
    }
  };

  const toggleFeatured = async (carId) => {
    try {
      const car = cars.find(c => c.id === carId);
      if (car) {
        const success = updateCar({ ...car, featured: !car.featured });
        if (success) {
          setSuccessMessage(`Car ${!car.featured ? 'featured' : 'unfeatured'} successfully!`);
        }
      }
    } catch (error) {
      console.error('Error toggling featured status:', error);
    }
  };

  // Notification components
  const Notification = ({ message, type, onClose }) => (
    <div className={`notification ${type}`}>
      <span className="notification-message">{message}</span>
      <button className="notification-close" onClick={onClose}>×</button>
    </div>
  );

  if (!isLoggedIn) {
    return (
      <div className="admin-login-wrapper">
        <div className="admin-login">
          <div className="admin-login-container">
            <div className="admin-login-card">
              <div className="admin-login-header">
                <h1>Admin Portal</h1>
                <p>Access the car rental management system</p>
              </div>
              <form onSubmit={handleLogin} className="admin-login-form">
                <div className="form-group">
                  <label className="form-label">Admin Password</label>
                  <input
                    type="password"
                    className="form-input"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter admin password"
                    required
                  />
                </div>
                <button type="submit" className="btn btn-primary btn-full">
                  Login to Admin Panel
                </button>
              </form>
              <div className="admin-login-footer">
                <small>Default password: admin123</small>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      {/* Notifications */}
      {error && (
        <Notification 
          message={error} 
          type="error" 
          onClose={clearError}
        />
      )}
      {successMessage && (
        <Notification 
          message={successMessage} 
          type="success" 
          onClose={() => setSuccessMessage('')}
        />
      )}

      <AdminNavbar onLogout={handleLogout} />

      <div className="admin-container">
        {/* Loading State */}
        {loading && (
          <div className="loading-overlay">
            <div className="loading-spinner">
              <div className="spinner"></div>
              <p>Loading cars data...</p>
            </div>
          </div>
        )}

        {/* Section Navigation */}
        <div className="section-nav">
          <button 
            className={`section-nav-btn ${activeSection === 'dashboard' ? 'active' : ''}`}
            onClick={() => setActiveSection('dashboard')}
          >
            Dashboard
          </button>
          <button 
            className={`section-nav-btn ${activeSection === 'cars' ? 'active' : ''}`}
            onClick={() => setActiveSection('cars')}
          >
            Cars
          </button>
        </div>

        {/* Dashboard Section */}
        {activeSection === 'dashboard' && (
          <>
            {/* Dashboard Title */}
            <div className="dashboard-title">
              <h1>CHALYATI</h1>
              <p>Manage your fleet and monitor performance</p>
            </div>

            {/* Dashboard Statistics */}
            <div className="dashboard-stats">
              <div className="stat-card">
                <div className="stat-icon">Total</div>
                <div className="stat-content">
                  <h3>Total Cars</h3>
                  <p className="stat-number">{totalCars}</p>
                </div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">Featured</div>
                <div className="stat-content">
                  <h3>Featured Cars</h3>
                  <p className="stat-number">{featuredCars}</p>
                </div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">Value</div>
                <div className="stat-content">
                  <h3>Total Value</h3>
                  <p className="stat-number">₹{totalValue.toLocaleString()}</p>
                </div>
              </div>
              <div className="stat-card">
                <div className="stat-icon">Average</div>
                <div className="stat-content">
                  <h3>Avg. Price/Day</h3>
                  <p className="stat-number">₹{avgPrice.toLocaleString()}</p>
                </div>
              </div>
            </div>

            {/* Quick Actions */}
            <div className="quick-actions">
              <button 
                onClick={() => setShowForm(!showForm)} 
                className="btn btn-primary"
              >
                {showForm ? 'Close Form' : 'Add New Car'}
              </button>
              <button 
                onClick={() => setShowForm(true)} 
                className="btn btn-secondary"
                disabled={!editingCar}
              >
                Edit Mode
              </button>
            </div>

            {/* Add/Edit Form */}
            {showForm && (
              <div className="admin-form">
                <h2>{editingCar ? 'Edit Car' : 'Add New Car'}</h2>
                <form onSubmit={handleSubmit} className="car-form">
                  <div className="form-row">
                    <div className="form-group">
                      <label className="form-label">Title *</label>
                      <input
                        type="text"
                        className="form-input"
                        name="title"
                        value={formData.title}
                        onChange={handleInputChange}
                        placeholder="Car title"
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">Brand *</label>
                      <select
                        className="form-select"
                        name="brand"
                        value={formData.brand}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select Brand</option>
                        {brands.map(brand => (
                          <option key={brand} value={brand}>{brand}</option>
                        ))}
                      </select>
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label className="form-label">Type *</label>
                      <select
                        className="form-select"
                        name="type"
                        value={formData.type}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select Type</option>
                        {types.map(type => (
                          <option key={type} value={type}>{type}</option>
                        ))}
                      </select>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Transmission *</label>
                      <select
                        className="form-select"
                        name="transmission"
                        value={formData.transmission}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select Transmission</option>
                        {transmissions.map(trans => (
                          <option key={trans} value={trans}>{trans}</option>
                        ))}
                      </select>
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label className="form-label">Fuel *</label>
                      <select
                        className="form-select"
                        name="fuel"
                        value={formData.fuel}
                        onChange={handleInputChange}
                        required
                      >
                        <option value="">Select Fuel</option>
                        {fuels.map(fuel => (
                          <option key={fuel} value={fuel}>{fuel}</option>
                        ))}
                      </select>
                    </div>

                    <div className="form-group">
                      <label className="form-label">Price per Day (₹) *</label>
                      <input
                        type="number"
                        className="form-input"
                        name="pricePerDay"
                        value={formData.pricePerDay}
                        onChange={handleInputChange}
                        min="0"
                        placeholder="0"
                        required
                      />
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label className="form-label">Seats *</label>
                      <input
                        type="number"
                        className="form-input"
                        name="seats"
                        value={formData.seats}
                        onChange={handleInputChange}
                        min="1"
                        max="10"
                        placeholder="5"
                        required
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">Mileage (km) *</label>
                      <input
                        type="number"
                        className="form-input"
                        name="mileageKm"
                        value={formData.mileageKm}
                        onChange={handleInputChange}
                        min="0"
                        placeholder="0"
                        required
                      />
                    </div>
                  </div>

                  <div className="form-row">
                    <div className="form-group">
                      <label className="form-label">Year of Manufacture</label>
                      <input
                        type="number"
                        className="form-input"
                        name="yearOfManufacture"
                        value={formData.yearOfManufacture}
                        onChange={handleInputChange}
                        min="1990"
                        max={new Date().getFullYear() + 1}
                        placeholder="2024"
                      />
                    </div>

                    <div className="form-group">
                      <label className="form-label">Owner Name</label>
                      <input
                        type="text"
                        className="form-input"
                        name="ownerName"
                        value={formData.ownerName}
                        onChange={handleInputChange}
                        placeholder="Owner name"
                      />
                    </div>
                  </div>

                  <div className="form-group">
                    <label className="form-label">Contact Number</label>
                    <input
                      type="text"
                      className="form-input"
                      name="contactNumber"
                      value={formData.contactNumber}
                      onChange={handleInputChange}
                      placeholder="Contact number"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Images (comma-separated URLs)</label>
                    <input
                      type="text"
                      className="form-input"
                      name="images"
                      value={formData.images}
                      onChange={handleInputChange}
                      placeholder="https://example.com/image1.jpg, https://example.com/image2.jpg"
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Description</label>
                    <textarea
                      className="form-textarea"
                      name="description"
                      value={formData.description}
                      onChange={handleInputChange}
                      rows="3"
                      placeholder="Describe the car's features and benefits..."
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-checkbox">
                      <input
                        type="checkbox"
                        name="featured"
                        checked={formData.featured}
                        onChange={handleInputChange}
                      />
                      <span className="checkmark"></span>
                      Featured Car
                    </label>
                  </div>

                  <div className="form-actions">
                    <button type="submit" className="btn btn-primary" disabled={loading}>
                      {loading ? 'Processing...' : (editingCar ? 'Update Car' : 'Add Car')}
                    </button>
                    {editingCar && (
                      <button type="button" className="btn btn-secondary" onClick={resetForm}>
                        Cancel Edit
                      </button>
                    )}
                  </div>
                </form>
              </div>
            )}
          </>
        )}

        {/* Cars Section */}
        {activeSection === 'cars' && (
          <>
            {/* Cars Title */}
            <div className="section-title">
              <h1>Car Inventory</h1>
              <p>Manage and view all cars in your fleet</p>
            </div>

            {/* Search and Filters */}
            <div className="search-filters">
              <div className="search-box">
                <input
                  type="text"
                  placeholder="Search cars by title or brand..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="search-input"
                />
              </div>
              <div className="filter-controls">
                <select
                  value={filterType}
                  onChange={(e) => setFilterType(e.target.value)}
                  className="filter-select"
                >
                  <option value="all">All Types</option>
                  {types.map(type => (
                    <option key={type} value={type}>{type}</option>
                  ))}
                </select>
                <select
                  value={filterBrand}
                  onChange={(e) => setFilterBrand(e.target.value)}
                  className="filter-select"
                >
                  <option value="all">All Brands</option>
                  {brands.map(brand => (
                    <option key={brand} value={brand}>{brand}</option>
                  ))}
                </select>
              </div>
            </div>

            {/* Cars List */}
            <div className="cars-list">
              <h2>Car Inventory ({filteredCars.length} of {totalCars})</h2>
              <div className="cars-grid">
                {filteredCars.map(car => (
                  <div key={car.id} className="car-item">
                    <div className="car-item-header">
                      <h3>{car.title}</h3>
                      <div className="car-badges">
                        {car.featured && <span className="badge featured">Featured</span>}
                        <span className="badge type">{car.type}</span>
                        <span className="badge brand">{car.brand}</span>
                      </div>
                    </div>
                    <div className="car-item-details">
                      <p><strong>Price:</strong> ₹{car.pricePerDay}/day</p>
                      <p><strong>Seats:</strong> {car.seats}</p>
                      <p><strong>Transmission:</strong> {car.transmission}</p>
                      <p><strong>Fuel:</strong> {car.fuel}</p>
                      <p><strong>Mileage:</strong> {car.mileageKm.toLocaleString()} km</p>
                      {car.yearOfManufacture && (
                        <p><strong>Year:</strong> {car.yearOfManufacture}</p>
                      )}
                      {car.ownerName && (
                        <p><strong>Owner:</strong> {car.ownerName}</p>
                      )}
                    </div>
                    <div className="car-actions">
                      <button 
                        className="btn btn-secondary btn-sm"
                        onClick={() => handleEdit(car)}
                      >
                        Edit
                      </button>
                      <button 
                        className="btn btn-warning btn-sm"
                        onClick={() => toggleFeatured(car.id)}
                      >
                        {car.featured ? 'Unfeature' : 'Feature'}
                      </button>
                      <button 
                        className="btn btn-danger btn-sm"
                        onClick={() => handleDelete(car.id)}
                      >
                        Delete
                      </button>
                    </div>
                  </div>
                ))}
              </div>
              {filteredCars.length === 0 && (
                <div className="no-results">
                  <p>No cars found matching your search criteria.</p>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Admin;
