import React, { createContext, useContext, useEffect, useReducer } from 'react';
import { seedCars } from '../store/seed';

const CarsContext = createContext();

const initialState = {
  cars: [],
  lastId: 0,
  loading: false,
  error: null
};

const carsReducer = (state, action) => {
  switch (action.type) {
    case 'SET_LOADING':
      return {
        ...state,
        loading: action.payload
      };
    case 'SET_ERROR':
      return {
        ...state,
        error: action.payload,
        loading: false
      };
    case 'CLEAR_ERROR':
      return {
        ...state,
        error: null
      };
    case 'INIT_CARS':
      return {
        ...state,
        cars: action.payload,
        lastId: action.payload.length,
        loading: false,
        error: null
      };
    case 'ADD_CAR':
      const newCar = {
        ...action.payload,
        id: String(state.lastId + 1),
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString()
      };
      return {
        ...state,
        cars: [...state.cars, newCar],
        lastId: state.lastId + 1,
        error: null
      };
    case 'UPDATE_CAR':
      return {
        ...state,
        cars: state.cars.map(car => 
          car.id === action.payload.id 
            ? { ...action.payload, updatedAt: new Date().toISOString() }
            : car
        ),
        error: null
      };
    case 'DELETE_CAR':
      return {
        ...state,
        cars: state.cars.filter(car => car.id !== action.payload),
        error: null
      };
    case 'BULK_UPDATE':
      return {
        ...state,
        cars: action.payload,
        error: null
      };
    default:
      return state;
  }
};

export const CarsProvider = ({ children }) => {
  const [state, dispatch] = useReducer(carsReducer, initialState);

  // Initialize cars from localStorage or seed data
  useEffect(() => {
    const initializeCars = async () => {
      dispatch({ type: 'SET_LOADING', payload: true });

      try {
        // Force load from seed data for now to debug
        console.log('Seed cars available:', seedCars.length);
        console.log('Seed cars:', seedCars);

        // Temporarily force load from seed data
        dispatch({ type: 'INIT_CARS', payload: seedCars });

        // Comment out localStorage logic for debugging
        /*
        const savedCars = localStorage.getItem('cars-state-v2');
        if (savedCars) {
          try {
            const parsed = JSON.parse(savedCars);
            console.log('Loading cars from localStorage:', parsed.cars.length);
            dispatch({ type: 'INIT_CARS', payload: parsed.cars });
          } catch (error) {
            console.error('Error parsing saved cars:', error);
            console.log('Loading cars from seed data:', seedCars.length);
            dispatch({ type: 'INIT_CARS', payload: seedCars });
          }
        } else {
          console.log('No saved cars found, loading from seed data:', seedCars.length);
          dispatch({ type: 'INIT_CARS', payload: seedCars });
        }
        */
      } catch (error) {
        console.error('Error initializing cars:', error);
        dispatch({ type: 'SET_ERROR', payload: 'Failed to load cars data' });
      }
    };

    initializeCars();
  }, []);

  // Persist to localStorage whenever cars change
  useEffect(() => {
    if (state.cars.length > 0) {
      try {
        localStorage.setItem('cars-state-v2', JSON.stringify({
          cars: state.cars,
          lastId: state.lastId
        }));
      } catch (error) {
        console.error('Error saving to localStorage:', error);
        dispatch({ type: 'SET_ERROR', payload: 'Failed to save data locally' });
      }
    }
  }, [state.cars, state.lastId]);

  // Validation function
  const validateCarData = (carData) => {
    const errors = [];

    if (!carData.title || carData.title.trim().length < 2) {
      errors.push('Title must be at least 2 characters long');
    }

    if (!carData.brand || carData.brand.trim().length === 0) {
      errors.push('Brand is required');
    }

    if (!carData.type || carData.type.trim().length === 0) {
      errors.push('Type is required');
    }

    if (!carData.transmission || carData.transmission.trim().length === 0) {
      errors.push('Transmission is required');
    }

    if (!carData.fuel || carData.fuel.trim().length === 0) {
      errors.push('Fuel type is required');
    }

    // Validate fuel type is one of the allowed values
    const allowedFuelTypes = ['Petrol', 'Diesel', 'EV', 'Hybrid'];
    if (!allowedFuelTypes.includes(carData.fuel)) {
      errors.push('Fuel type must be one of: Petrol, Diesel, EV, or Hybrid');
    }

    if (!carData.pricePerDay || isNaN(carData.pricePerDay) || carData.pricePerDay < 0) {
      errors.push('Price per day must be a positive number');
    }

    if (!carData.seats || isNaN(carData.seats) || carData.seats < 1 || carData.seats > 10) {
      errors.push('Seats must be between 1 and 10');
    }

    if (!carData.mileageKm || isNaN(carData.mileageKm) || carData.mileageKm < 0) {
      errors.push('Mileage must be a positive number');
    }

    // Validate year of manufacture
    if (carData.yearOfManufacture) {
      const currentYear = new Date().getFullYear();
      if (isNaN(carData.yearOfManufacture) || 
          carData.yearOfManufacture < 1990 || 
          carData.yearOfManufacture > currentYear + 1) {
        errors.push(`Year of manufacture must be between 1990 and ${currentYear + 1}`);
      }
    }

    // Validate owner information
    if (!carData.ownerName || carData.ownerName.trim().length === 0) {
      errors.push('Owner name is required');
    }

    if (!carData.contactNumber || carData.contactNumber.trim().length === 0) {
      errors.push('Contact number is required');
    }

    // Validate contact number format (basic Indian phone number validation)
    if (carData.contactNumber && carData.contactNumber !== '—') {
      const phoneRegex = /^[6-9]\d{9}$/;
      if (!phoneRegex.test(carData.contactNumber.replace(/\D/g, ''))) {
        errors.push('Contact number must be a valid 10-digit Indian mobile number');
      }
    }

    // Validate images array
    if (carData.images && Array.isArray(carData.images) && carData.images.length === 0) {
      errors.push('At least one image is required');
    }

    return errors;
  };

  const addCar = (carData) => {
    try {
      const errors = validateCarData(carData);
      if (errors.length > 0) {
        dispatch({ type: 'SET_ERROR', payload: errors.join(', ') });
        return false;
      }

      // Process images
      const processedCarData = {
        ...carData,
        images: carData.images ? carData.images.split(',').map(img => img.trim()).filter(img => img) : [],
        pricePerDay: parseInt(carData.pricePerDay) || 0,
        seats: parseInt(carData.seats) || 5,
        mileageKm: parseInt(carData.mileageKm) || 0,
        yearOfManufacture: carData.yearOfManufacture ? parseInt(carData.yearOfManufacture) : null,
        featured: Boolean(carData.featured)
      };

      dispatch({ type: 'ADD_CAR', payload: processedCarData });
      return true;
    } catch (error) {
      console.error('Error adding car:', error);
      dispatch({ type: 'SET_ERROR', payload: 'Failed to add car' });
      return false;
    }
  };

  const updateCar = (carData) => {
    try {
      const errors = validateCarData(carData);
      if (errors.length > 0) {
        dispatch({ type: 'SET_ERROR', payload: errors.join(', ') });
        return false;
      }

      // Process images
      const processedCarData = {
        ...carData,
        images: carData.images ? carData.images.split(',').map(img => img.trim()).filter(img => img) : [],
        pricePerDay: parseInt(carData.pricePerDay) || 0,
        seats: parseInt(carData.seats) || 5,
        mileageKm: parseInt(carData.mileageKm) || 0,
        yearOfManufacture: carData.yearOfManufacture ? parseInt(carData.yearOfManufacture) : null,
        featured: Boolean(carData.featured)
      };

      dispatch({ type: 'UPDATE_CAR', payload: processedCarData });
      return true;
    } catch (error) {
      console.error('Error updating car:', error);
      dispatch({ type: 'SET_ERROR', payload: 'Failed to update car' });
      return false;
    }
  };

  const deleteCar = (carId) => {
    try {
      dispatch({ type: 'DELETE_CAR', payload: carId });
      return true;
    } catch (error) {
      console.error('Error deleting car:', error);
      dispatch({ type: 'SET_ERROR', payload: 'Failed to delete car' });
      return false;
    }
  };

  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  const getCarById = (id) => {
    return state.cars.find(car => car.id === id);
  };

  const getCarsByBrand = (brand) => {
    return state.cars.filter(car => car.brand === brand);
  };

  const getCarsByType = (type) => {
    return state.cars.filter(car => car.type === type);
  };

  const getFeaturedCars = () => {
    return state.cars.filter(car => car.featured);
  };

  const getCarsByPriceRange = (minPrice, maxPrice) => {
    return state.cars.filter(car => 
      car.pricePerDay >= minPrice && car.pricePerDay <= maxPrice
    );
  };

  const searchCars = (query) => {
    const searchTerm = query.toLowerCase();
    return state.cars.filter(car =>
      car.title.toLowerCase().includes(searchTerm) ||
      car.brand.toLowerCase().includes(searchTerm) ||
      car.description?.toLowerCase().includes(searchTerm)
    );
  };

  const value = {
    cars: state.cars,
    loading: state.loading,
    error: state.error,
    addCar,
    updateCar,
    deleteCar,
    clearError,
    getCarById,
    getCarsByBrand,
    getCarsByType,
    getFeaturedCars,
    getCarsByPriceRange,
    searchCars
  };

  return (
    <CarsContext.Provider value={value}>
      {children}
    </CarsContext.Provider>
  );
};

export const useCars = () => {
  const context = useContext(CarsContext);
  if (!context) {
    throw new Error('useCars must be used within a CarsProvider');
  }
  return context;
};
