import React from 'react';
import FAQ from "../components/FAQ";
import FeaturedCarsBlock from "../components/FeaturedCarsBlock";
import Hero from '../components/Hero';
import Testimonials from "../components/Testimonials";
import USPGrid from "../components/USPGrid";


const Home = () => {

  return (
    <div className="main">
      <div className="container">
        {/* Hero Section */}
        <Hero />

        {/* Extra Sections (just added, not replacing anything) */}
        <USPGrid />
        <FeaturedCarsBlock />
        <Testimonials />
        <FAQ />

        {/* Special Offers */}
        <section className="offers-section full-viewport">
          <h2 className="section-title">Special Offers</h2>
          <div className="offers-grid">
            {[
              "Weekly and monthly rental discounts",
              "Corporate fleet rental packages",
              "Long-term rental special rates",
              "Weekend getaway packages",
              "Student and senior citizen discounts",
              "Loyalty program benefits",
            ].map((offer, idx) => (
              <div key={idx} className="offer-card">
                <div className="offer-icon">âœ“</div>
                <p className="offer-text">{offer}</p>
              </div>
            ))}
          </div>
        </section>
      </div>
    </div>
  );
};

export default Home;
