import React from "react";
import ScrollStack, { ScrollStackItem } from "./ScrollStack";

const items = [
  {
    name: "Samarth Chaturvedi",
    text:
      "Took a Chalyati subscription for 6 monthsâ€”smooth process, car in great condition, and very helpful support.",
    rating: 4.5,
  },
  {
    name: "Vaibhav Kaushal",
    text:
      "Awesome service and quick delivery within 5 days. Better pricing compared to competitors.",
    rating: 5,
  },
  {
    name: "Rhonen Gurung",
    text:
      "Using for about a monthâ€”car delivered in peak condition, team is prompt and professional.",
    rating: 5,
  },
];

const StarRow = ({ rating }) => {
  const full = Math.floor(rating);
  const half = rating % 1 >= 0.5;
  return (
    <div style={{ display: "flex", gap: 4, marginBottom: "1rem" }}>
      {Array.from({ length: full }).map((_, i) => <span key={`f${i}`} style={{ color: "#FFD700", fontSize: "1.2rem" }}>â˜…</span>)}
      {half && <span style={{ color: "#FFD700", fontSize: "1.2rem" }}>â˜†</span>}
    </div>
  );
};

const Testimonials = () => (
  <section className="full-viewport" style={{ 
    height: "100vh", 
    background: "#000000",
    position: "relative",
    overflow: "hidden"
  }}>
    <div style={{ 
      padding: "2rem 0", 
      textAlign: "center",
      position: "absolute",
      top: "2rem",
      left: "50%",
      transform: "translateX(-50%)",
      zIndex: 10,
      width: "100%"
    }}>
      <h2 className="section-title" style={{ color: "white", marginBottom: "3rem" }}>Our customers rate us highly</h2>
    </div>

    <div style={{ 
      height: "100vh", 
      maxWidth: "800px", 
      margin: "0 auto",
      position: "relative",
      paddingTop: "8rem"
    }}>
      <ScrollStack
        itemDistance={120}
        itemScale={0.08}
        itemStackDistance={60}
        stackPosition="30%"
        scaleEndPosition="20%"
        baseScale={0.7}
        rotationAmount={3}
        blurAmount={1.5}
        className="testimonials-scroll-stack"
      >
        {items.map((t, i) => (
          <ScrollStackItem key={i} itemClassName="testimonial-card">
            <div style={{ 
              background: "white", 
              borderRadius: "20px", 
              padding: "2rem", 
              height: "100%",
              display: "flex",
              flexDirection: "column",
              justifyContent: "space-between",
              boxShadow: "0 10px 40px rgba(0,0,0,0.3)",
              border: "1px solid rgba(255,255,255,0.1)"
            }}>
              <div>
                <div style={{ fontSize: "3rem", marginBottom: "1rem" }}>ðŸ™‚</div>
                <h3 style={{ 
                  fontSize: "1.5rem", 
                  fontWeight: "bold", 
                  marginBottom: "0.5rem",
                  color: "#333"
                }}>
                  {t.name}
                </h3>
                <StarRow rating={t.rating} />
                <p style={{ 
                  fontSize: "1.1rem", 
                  lineHeight: "1.6", 
                  color: "#666",
                  margin: 0
                }}>
                  {t.text}
                </p>
              </div>
            </div>
          </ScrollStackItem>
        ))}
      </ScrollStack>
    </div>
  </section>
);

export default Testimonials;
