import React from "react";
import { Link } from "react-router-dom";
import { useCars } from "../state/CarsContext";
import { getCarImageUrl } from "../utils/imageUtils";

const FeaturedCarsBlock = () => {
  const { cars } = useCars();
  const featured = cars.filter(c => c.featured).slice(0, 6);

  return (
    <section className="full-viewport">
      <div className="results-header" style={{ background: "transparent", borderBottom: "none", padding: 0 }}>
        <h2 className="section-title" style={{ marginBottom: 0 }}>Featured Cars</h2>
      </div>

      {featured.length ? (
        <div className="cars-grid" style={{ marginTop: "2rem" }}>
          {featured.map(car => (
            <div key={car._id} className="car-card">
              <img 
                className="car-image" 
                src={getCarImageUrl(car.images?.[0])} 
                alt={car.title}
                onError={(e) => {
                  console.warn(`Failed to load image for ${car.title}`);
                  e.target.src = '/img/placeholder.jpg';
                }}
              />
              <div className="car-content">
                <div className="car-header">
                  <h3 className="car-title">{car.title}</h3>
                  <div className="car-price">₹{car.pricePerDay?.toLocaleString() || "—"} <span className="focus-car-price-unit">/day</span></div>
                </div>
                <div className="car-specs">
                  <span className="spec-chip">{car.brand}</span>
                  <span className="spec-chip">{car.fuel}</span>
                  <span className="spec-chip">{car.transmission}</span>
                  <span className="spec-chip">{car.type}</span>
                </div>
                <div className="focus-car-actions">
                  <Link to={`/cars/${car._id}`} className="focus-car-button">View</Link>
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="empty-state" style={{ margin: 0 }}>
          <h3>No featured cars right now</h3>
          <p>New vehicles are added often—check back soon.</p>
        </div>
      )}

      <div style={{ textAlign: "center", marginTop: "2rem" }}>
        <Link to="/cars" className="btn btn-secondary">View All</Link>
      </div>
    </section>
  );
};

export default FeaturedCarsBlock;
