import React, { useState } from 'react';
import { cn } from '../lib/utils';

export const Card = React.memo(
  ({
    car,
    index,
    hovered,
    setHovered,
  }) => {
    const { id, title, brand, type, transmission, fuel, pricePerDay, seats, mileageKm, images, featured, description } = car;
    const imageUrl = images && images.length > 0 ? images[0] : '/img/placeholder.jpg';

    return (
      <div
        onMouseEnter={() => setHovered(index)}
        onMouseLeave={() => setHovered(null)}
        style={{
          borderRadius: '8px',
          position: 'relative',
          backgroundColor: '#1a1a1a',
          overflow: 'hidden',
          height: '300px',
          width: '100%',
          transition: 'all 0.3s ease-out',
          cursor: 'pointer',
          filter: hovered !== null && hovered !== index ? 'blur(2px)' : 'none',
          transform: hovered !== null && hovered !== index ? 'scale(0.98)' : 'scale(1)',
          border: '1px solid #2a2a2a'
        }}
      >
        <img
          src={imageUrl}
          alt={title}
          style={{
            objectFit: 'cover',
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%'
          }}
          onError={(e) => {
            e.target.src = '/img/placeholder.jpg';
          }}
        />

        {/* Overlay with car info - only visible on hover */}
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            alignItems: 'flex-end',
            padding: '2rem 1rem',
            transition: 'opacity 0.3s',
            opacity: hovered === index ? 1 : 0
          }}
        >
          <div style={{
            fontSize: '1.5rem',
            fontWeight: '500',
            color: '#ffffff',
            textShadow: '2px 2px 4px rgba(0, 0, 0, 0.8)'
          }}>
            {title}
          </div>
        </div>

        {/* Featured badge - always visible */}
        {featured && (
          <div style={{
            position: 'absolute',
            top: '1rem',
            right: '1rem',
            backgroundColor: '#fbbf24',
            color: '#000000',
            padding: '0.25rem 0.75rem',
            borderRadius: '9999px',
            fontSize: '0.875rem',
            fontWeight: '600',
            zIndex: 10
          }}>
            Featured
          </div>
        )}
      </div>
    );
  }
);

Card.displayName = 'Card';

export function FocusCarCards({ cars }) {
  const [hovered, setHovered] = useState(null);

  console.log('FocusCarCards received cars:', cars);
  console.log('Cars length:', cars?.length);

  if (!cars || cars.length === 0) {
    return <div>No cars to display</div>;
  }

  return (
    <div style={{
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
      gap: '2rem',
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '0 1rem'
    }}>
      {cars.map((car, index) => {
        console.log('Rendering car:', car.title, 'at index:', index);
        return (
          <Card
            key={car.id}
            car={car}
            index={index}
            hovered={hovered}
            setHovered={setHovered}
          />
        );
      })}
    </div>
  );
}
