import React from "react";
import InfiniteScroll from './InfiniteScroll';

const testimonials = [
  {
    name: "Samarth Chaturvedi",
    text: "Took a Chalyati subscription for 6 months—smooth process, car in great condition, and very helpful support.",
    rating: 4.5,
    avatar: "👨‍💼"
  },
  {
    name: "Vaibhav Kaushal", 
    text: "Awesome service and quick delivery within 5 days. Better pricing compared to competitors.",
    rating: 5,
    avatar: "👨‍🎓"
  },
  {
    name: "Rhonen Gurung",
    text: "Using for about a month—car delivered in peak condition, team is prompt and professional.",
    rating: 5,
    avatar: "👨‍🚀"
  },
  {
    name: "Priya Sharma",
    text: "Excellent customer service and well-maintained vehicles. Highly recommend for business travel.",
    rating: 5,
    avatar: "👩‍💻"
  },
  {
    name: "Rajesh Kumar",
    text: "Great experience with Chalyati. The booking process is simple and cars are always clean.",
    rating: 4.5,
    avatar: "👨‍💻"
  },
  {
    name: "Anita Singh",
    text: "Perfect for weekend getaways! Clean cars, easy booking, and great customer support.",
    rating: 5,
    avatar: "👩‍🎨"
  },
  {
    name: "Vikram Patel",
    text: "Corporate booking made simple. Reliable service for our business needs.",
    rating: 4.5,
    avatar: "👨‍💼"
  },
  {
    name: "Sneha Reddy",
    text: "Love the variety of cars available. Always get exactly what I need for my trips.",
    rating: 5,
    avatar: "👩‍💻"
  }
];

const StarRating = ({ rating }) => {
  const fullStars = Math.floor(rating);
  const hasHalfStar = rating % 1 >= 0.5;

  return (
    <div className="star-rating">
      {Array.from({ length: fullStars }).map((_, i) => (
        <span key={i} className="star">★</span>
      ))}
      {hasHalfStar && <span className="star half">☆</span>}
    </div>
  );
};

const TestimonialCard = ({ testimonial }) => (
  <div className="testimonial-card-infinite">
    <div className="testimonial-avatar">
      {testimonial.avatar}
    </div>

    <div className="testimonial-content">
      <StarRating rating={testimonial.rating} />

      <blockquote className="testimonial-text">
        "{testimonial.text}"
      </blockquote>

      <div className="testimonial-author">
        <strong>{testimonial.name}</strong>
      </div>
    </div>
  </div>
);

const Testimonials = () => {
  const items = testimonials.map((testimonial, index) => ({
    content: <TestimonialCard testimonial={testimonial} />
  }));

  return (
    <section className="testimonials-section">
      <div className="container">
        <div className="testimonials-header">
          <h2 className="section-title">What Our Customers Say</h2>
          <p className="testimonials-subtitle">Real reviews from satisfied customers</p>
        </div>

        <div className="testimonials-content">
          <div style={{height: '500px', position: 'relative'}}>
            <InfiniteScroll
              items={items}
              isTilted={true}
              tiltDirection='left'
              autoplay={true}
              autoplaySpeed={0.1}
              autoplayDirection="down"
              pauseOnHover={true}
              width="35rem"
              itemMinHeight={200}
            />
          </div>
        </div>
      </div>
    </section>
  );
};

export default Testimonials;
