import { motion, useAnimation, useMotionValue, useTransform } from 'motion/react';
import { useEffect, useRef, useState } from 'react';
import './RollingGallery.css';

const RollingGallery = ({ autoplay = false, pauseOnHover = false, images = [] }) => {
  const [isScreenSizeSm, setIsScreenSizeSm] = useState(window.innerWidth <= 640);

  // Fixed spacing approach for consistent gaps
  const faceCount = images.length;
  const fixedImageWidth = isScreenSizeSm ? 280 : 350; // Fixed image width
  const fixedGap = isScreenSizeSm ? 50 : 80; // Fixed gap between images
  const faceWidth = fixedImageWidth + fixedGap; // Total width per face including gap
  const cylinderWidth = faceWidth * faceCount; // Total cylinder width based on fixed spacing
  const dragFactor = 0.03; // Reduced drag sensitivity
  const radius = cylinderWidth / (2 * Math.PI);

  const rotation = useMotionValue(0);
  const controls = useAnimation();
  const autoplayRef = useRef();
  const isPaused = useRef(false);

  const handleDrag = (_, info) => {
    if (!isPaused.current) {
      rotation.set(rotation.get() + info.offset.x * dragFactor);
    }
  };

  const handleDragEnd = (_, info) => {
    if (!isPaused.current) {
      controls.start({
        rotateY: rotation.get() + info.velocity.x * dragFactor,
        transition: { type: 'spring', stiffness: 60, damping: 20, mass: 0.1, ease: 'easeOut' }
      });
    }
  };

  const transform = useTransform(rotation, value => {
    return `rotate3d(0, 1, 0, ${value}deg)`;
  });

  // Continuous smooth scrolling
  useEffect(() => {
    if (autoplay && !isPaused.current) {
      const startContinuousScroll = () => {
        autoplayRef.current = setInterval(() => {
          if (!isPaused.current) {
            const currentRotation = rotation.get();
            const newRotation = currentRotation - 0.2; // Slower smooth continuous movement

            controls.start({
              rotateY: newRotation,
              transition: { duration: 0.1, ease: 'linear' }
            });
            rotation.set(newRotation);
          }
        }, 16); // 60fps smooth animation
      };

      startContinuousScroll();
      return () => clearInterval(autoplayRef.current);
    }
  }, [autoplay, rotation, controls]);

  useEffect(() => {
    const handleResize = () => {
      setIsScreenSizeSm(window.innerWidth <= 640);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const handleMouseEnter = () => {
    if (autoplay && pauseOnHover) {
      isPaused.current = true;
      clearInterval(autoplayRef.current);
      controls.stop();
    }
  };

  const handleMouseLeave = () => {
    if (autoplay && pauseOnHover) {
      isPaused.current = false;
      // Restart continuous scrolling
      const startContinuousScroll = () => {
        autoplayRef.current = setInterval(() => {
          if (!isPaused.current) {
            const currentRotation = rotation.get();
            const newRotation = currentRotation - 0.2;

            controls.start({
              rotateY: newRotation,
              transition: { duration: 0.1, ease: 'linear' }
            });
            rotation.set(newRotation);
          }
        }, 16);
      };
      startContinuousScroll();
    }
  };

  return (
    <div className="gallery-container">
      <div className="gallery-gradient gallery-gradient-left"></div>
      <div className="gallery-gradient gallery-gradient-right"></div>
      <div className="gallery-content">
        <motion.div
          drag="x"
          className="gallery-track"
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
          style={{
            transform: transform,
            rotateY: rotation,
            width: cylinderWidth,
            transformStyle: 'preserve-3d'
          }}
          onDrag={handleDrag}
          onDragEnd={handleDragEnd}
          animate={controls}
        >
          {images.map((url, i) => (
            <div
              key={i}
              className="gallery-item"
              style={{
                width: `${faceWidth}px`,
                transform: `rotateY(${i * (360 / faceCount)}deg) translateZ(${radius}px) translateX(0px)`
              }}
            >
              <img src={url} alt="gallery" className="gallery-img" />
            </div>
          ))}
        </motion.div>
      </div>
    </div>
  );
};

export default RollingGallery;
