import React from "react";

const items = [
  {
    title: "Zero Down-Payment",
    desc: "Save heavy one-time commitments on car purchase.",
  },
  {
    title: "White Number Plate",
    desc: "Delivered with a standard white plate for private use.",
  },
  {
    title: "Service & Maintenance",
    desc: "Regular wear & tear and scheduled service are on us.",
  },
  {
    title: "24×7 RSA",
    desc: "Round-the-clock roadside assistance across India.",
  },
];

const USPGrid = () => (
  <section className="full-viewport">
    <h2 className="section-title">Own A Car The Smart Way</h2>
    <p className="hero-description" style={{ textAlign: "center", maxWidth: 800, margin: "0 auto 2rem" }}>
      All-inclusive used car subscription by <strong>Chalyati</strong>.
    </p>

    <div className="offers-grid">
      {items.map((it, i) => (
        <div key={i} className="offer-card">
          <div className="offer-icon">✓</div>
          <div>
            <h3 className="step-title" style={{ marginBottom: 6 }}>{it.title}</h3>
            <p className="offer-text">{it.desc}</p>
          </div>
        </div>
      ))}
    </div>
  </section>
);

export default USPGrid;
