import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { cn } from '../lib/utils';

export const FocusCarCard = React.memo(
  ({
    car,
    index,
    hovered,
    setHovered,
  }) => {
    const { id, title, brand, type, transmission, fuel, pricePerDay, seats, mileageKm, images, featured, description } = car;
    const imageUrl = images && images.length > 0 ? images[0] : '/img/placeholder.jpg';

    return (
      <div
        onMouseEnter={() => setHovered(index)}
        onMouseLeave={() => setHovered(null)}
        className={cn(
          "rounded-lg relative bg-gray-100 dark:bg-neutral-900 overflow-hidden h-80 w-full transition-all duration-300 ease-out cursor-pointer",
          hovered !== null && hovered !== index && "blur-sm scale-[0.98]"
        )}
      >
        <img
          src={imageUrl}
          alt={title}
          className="object-cover absolute inset-0 w-full h-full"
          onError={(e) => {
            e.target.src = '/img/placeholder.jpg';
          }}
        />

        {/* Overlay with car info */}
        <div
          className={cn(
            "absolute inset-0 bg-gradient-to-t from-black/80 via-black/40 to-transparent flex flex-col justify-end p-6 transition-opacity duration-300",
            hovered === index ? "opacity-100" : "opacity-90"
          )}
        >
          {/* Featured badge */}
          {featured && (
            <div className="absolute top-4 right-4 bg-yellow-500 text-black px-3 py-1 rounded-full text-sm font-semibold">
              Featured
            </div>
          )}

          {/* Car title and brand */}
          <div className="text-white mb-3">
            <h3 className="text-xl font-bold mb-1">{title}</h3>
            <p className="text-gray-300 text-sm">{brand}</p>
          </div>

          {/* Car specs */}
          <div className="flex flex-wrap gap-2 mb-4">
            <span className="bg-white/20 text-white px-2 py-1 rounded text-xs">{type}</span>
            <span className="bg-white/20 text-white px-2 py-1 rounded text-xs">{transmission}</span>
            <span className="bg-white/20 text-white px-2 py-1 rounded text-xs">{fuel}</span>
            <span className="bg-white/20 text-white px-2 py-1 rounded text-xs">{seats} seats</span>
          </div>

          {/* Price and action */}
          <div className="flex justify-between items-center">
            <div className="text-white">
              <span className="text-2xl font-bold">â‚¹{pricePerDay}</span>
              <span className="text-gray-300 text-sm">/day</span>
            </div>
            <Link 
              to={`/cars/${id}`}
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg font-medium transition-colors duration-200"
            >
              View Details
            </Link>
          </div>
        </div>
      </div>
    );
  }
);

FocusCarCard.displayName = 'FocusCarCard';

export function FocusCarCards({ cars }) {
  const [hovered, setHovered] = useState(null);

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8 max-w-7xl mx-auto w-full">
      {cars.map((car, index) => (
        <FocusCarCard
          key={car.id}
          car={car}
          index={index}
          hovered={hovered}
          setHovered={setHovered}
        />
      ))}
    </div>
  );
}
